define(["angular","cjt/util/locale"],function(angular,LOCALE){angular.module("App").directive("cpIssueList",[function(){var counter=0;return{templateUrl:"directives/issueList.phtml",scope:{issues:"=",idPrefix:"@"},link:function(scope,elem,attrs){if(angular.isDefined(scope.issues)&&!angular.isArray(scope.issues)){throw new TypeError("The issues attribute should evaluate to an array of issue objects.")}scope.$watch("idPrefix",function(newVal){if(!newVal&&newVal!==0){scope.idPrefix=counter++}});scope.getIssueTitle=function(issue){if(issue.title){return issue.title}if(issue.area==="quota"){switch(issue.service){case"email":return issue.type==="error"?LOCALE.maketext("Mail Quota Reached:"):LOCALE.maketext("Mail Quota Warning:");case"ftp":return issue.type==="error"?LOCALE.maketext("[asis,FTP] Quota Reached:"):LOCALE.maketext("[asis,FTP] Quota Warning:")}}else{return issue.type==="error"?LOCALE.maketext("Error:"):LOCALE.maketext("Warning:")}}}}}])});